package aoc2016;

/**
 *   [1] [2] [3]
 *   [4] [5] [6]
 *   [7] [8] [9]
 */

public class daytwo {
    public static void main(String[] args) {
        String[] inputArray = {"", "", "", "", ""};
        System.out.println(inputArray[0]);
        String[][] keypad = {
                {" ", " ", "1", " ", " "},
                {" ", "2", "3", "4", " "},
                {"5", "6", "7", "8", "9"},
                {" ", "A", "B", "C", " "},
                {" ", " ", "D", " ", " "}
        };
        int keypadY = 2;
        int keypadX = 0;

        for (int i = 0; i < inputArray.length; i++) {
            for (int j = 0; j < inputArray[i].length(); j++) {
                switch(inputArray[i].charAt(j)){
                    case 'U':
                        if (keypadY == 0) {
                        } else {
                            keypadY--;
                            if (keypad[keypadY][keypadX].equals(" ")){
                                keypadY++;
                            }
                        }
                        break;
                    case 'D':
                        if (keypadY == 4) {
                        } else {
                            keypadY++;
                            if (keypad[keypadY][keypadX].equals(" ")){
                                keypadY--;
                            }
                        }
                        break;
                    case 'L':
                        if (keypadX == 0) {
                        } else {
                            keypadX--;
                            if (keypad[keypadY][keypadX].equals(" ")){
                                keypadX++;
                            }
                        }
                        break;
                    case 'R':
                        if (keypadX == 4) {
                        } else {
                            keypadX++;
                            if (keypad[keypadY][keypadX].equals(" ")){
                                keypadX--;
                            }
                        }
                        break;
                }
            }
            System.out.println(keypad[keypadY][keypadX]);
        }
    }
}
